import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function UserWelcome() {
  const navigate = useNavigate();
  const [role, setRole] = useState('');
  const [username, setUsername] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }
    try {
      const payload = JSON.parse(atob(token.split('.')[1]));
      setRole(payload.role);
      setUsername(payload.username || '');
      if (payload.role === 'admin') {
        navigate('/admin/dashboard');
      }
    } catch {
      navigate('/login');
    }
  }, [navigate]);

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-indigo-800 via-purple-900 to-black text-white">
      <div className="bg-white/10 backdrop-blur-md p-10 rounded-xl shadow-2xl border border-white/10 text-center max-w-md w-full">
        <h1 className="text-4xl font-bold mb-4">Welcome {role ? role.charAt(0).toUpperCase() + role.slice(1) : ''}</h1>
        <p className="text-purple-200 mb-6 text-sm">You are now signed in to FRMS.</p>
        <button
          onClick={() => { localStorage.removeItem('token'); navigate('/login'); }}
          className="px-6 py-2 rounded-md bg-gradient-to-r from-pink-500 to-orange-400 font-semibold shadow hover:brightness-110 transition"
        >Logout</button>
      </div>
    </div>
  );
}
